import React from 'react';
import { useState, useEffect, useContext } from 'react';
import ScrollToBottom from "react-scroll-to-bottom";
import { UserContext } from '../../../userContext';
import Axios from "axios"
import '../Chats/Chats.css';

function Chats({socket, user_id, username}){

    const {user, setUser}  = useContext(UserContext);
    const [friends, setFriends] = useState(null)

    const [newMessage, setNewMessage] = useState("");
    const [messageList, setMessageList] = useState([]);

    const sendMessage = async () => {
        if (newMessage !== "") {
          const mData = {
            room : '123',
            author : username,
            message : newMessage,
            time : new Date(Date.now()).getHours() + ":" + new Date(Date.now()).getMinutes()
        };

        await socket.emit("send_message", mData);

          setMessageList((list) => [...list, mData]);
          setNewMessage("");
        }
      };

      const friendlist = ()=>{
          console.log(username);
        Axios.post("http://localhost:8080/friendlist").then((response) => {
          if(response.data.length === 0){
            console.log("does not Exists!");
          }
          else{
            setFriends(response.data.map(obj => <li className="list" onClick={() => selected(obj.user_id)}><a href='#'>{obj.username + ' (' + obj.user_id + ')'}</a></li>))
          }
        });
      };

      const joinRoom = (room) => {
        socket.emit("join_room", room);
      };

      const createChatRoom = (e)=>{
        console.log(e);
        console.log(user_id);
      Axios.post("http://localhost:8080/createChatRoom", {
        user_id: user_id,
        friend_id: e
      }).then((response) => {
          console.log(response);
          console.log("createchatroom");
        // if(response.data.code === "ER_BAD_FIELD_ERROR"){
        //   console.log("does not Exists!");
        // }
        // else{
        //   joinRoom();
        // }
      });
    };


      const chatRoom = (e)=>{
          console.log(e);
          console.log(user_id);
        Axios.post("http://localhost:8080/chatRoom", {
          user_id: user_id,
          friend_id: e
        }).then((response) => {
            console.log("chatroom");
          if(response.data.code === "ER_BAD_FIELD_ERROR"){
            console.log("does not Exists!");
            createChatRoom
          }
          else{
            joinRoom();
          }
        });
      };

      function selected(e){
        chatRoom(e);
    }

      useEffect(() => {
        socket.on("receive_message", (data) => {
            console.log(data);
          setMessageList((list) => [...list, data]);
        });
      }, [socket]);

    return(
        <div className='outerBox'>
            <div className="leftColumn">
                <div className="upperLeft">
                    <img src="https://www.kindpng.com/picc/m/690-6904538_men-profile-icon-png-image-free-download-searchpng.png" alt="" />
                    <p className="userName">Muhammad Azeem</p>
                </div>
                <div className="lowerLeft">
                    <p className="title">About:</p>
                    <p className="details">Too lazy</p>
                    <p className="title">Phone Number:</p>
                    <p className="details">0314114714</p>
                    <p className="title">Email:</p>
                    <p className="details">mazeem8856@gmail.com</p>
                    <p className="title">Status:</p>
                    <p className="details">Single</p>
                </div>
            </div>
            <div className="centerColumn">
            <div className="chat-body">
                <ScrollToBottom className="message-container">
                {messageList.map((messageContent) => {
                    return (
                    <div className="message" id={username === messageContent.author ? "you" : "other"}>
                        <div>
                            <div className="message-content">
                                <p>{messageContent.message}</p>
                            </div>
                            <div className="message-meta">
                                <p id="time">{messageContent.time}</p>
                                <p id="author">{messageContent.author}</p>
                            </div>
                        </div>
                    </div>
                    );
                })}
                </ScrollToBottom>
        	</div> 
                <div className="messageBox">
                    <div className="messageText">
                        <input type="text" placeholder="Write Message..." value={newMessage} onChange={(event)=>{
                            setNewMessage(event.target.value);
                        }} onKeyPress={(event) => {
                            event.key === "Enter" && sendMessage();
                        }}/>
                    </div>
                    <div className="sendButton">
                        <button type='submit' onClick={sendMessage}><ion-icon size="large" name="send"></ion-icon></button>
                    </div>
                </div>
            </div>
            <div className="rightColumn">
                <div className="upperRight">
                    <p>Friends</p>
                    <button type="button" onClick={friendlist}>reload</button>
                </div>
                <div className="lowerRight">
                    {friends ? friends: null}
                </div>
            </div>
        </div>
    );
}

export default Chats;
